import React, { useState, useEffect } from 'react';
import { axiosConfig } from '../../services/axiosConfig';
import '../../styles/modal.css';

const PlantSchedule = ({ plantId, isOpen, onClose }) => {
  const [schedule, setSchedule] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (isOpen && plantId) {
      fetchSchedule();
    }
  }, [isOpen, plantId]);

  const fetchSchedule = async () => {
    setLoading(true);
    try {
      const response = await axiosConfig.get(`/plants/${plantId}/schedule`);
      setSchedule(response.data);
    } catch (error) {
      console.error('Error fetching schedule:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      weekday: 'short',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getDaysText = (days) => {
    if (days < 0) return `${Math.abs(days)} days overdue`;
    if (days === 0) return 'Today';
    if (days === 1) return 'Tomorrow';
    return `In ${days} days`;
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Plant Care Schedule</h2>
        {loading ? (
          <p>Loading schedule...</p>
        ) : schedule ? (
          <div style={{ display: 'grid', gap: '20px' }}>
            <div style={{ 
              padding: '16px', 
              backgroundColor: '#e6f3ff', 
              borderRadius: '8px',
              border: '1px solid #b3d9ff'
            }}>
              <h3 style={{ margin: '0 0 8px 0', color: '#0066cc' }}>
                ðŸ’§ Next Watering
              </h3>
              <p style={{ margin: '4px 0', fontSize: '14px' }}>
                <strong>{formatDate(schedule.nextWatering)}</strong>
              </p>
              <p style={{ 
                margin: '4px 0', 
                fontSize: '12px', 
                color: schedule.wateringDaysLeft < 0 ? '#cc0000' : '#666'
              }}>
                {getDaysText(schedule.wateringDaysLeft)}
              </p>
            </div>

            <div style={{ 
              padding: '16px', 
              backgroundColor: '#f0f8e6', 
              borderRadius: '8px',
              border: '1px solid #c3e6a3'
            }}>
              <h3 style={{ margin: '0 0 8px 0', color: '#4d7c0f' }}>
                ðŸŒ± Next Fertilizer
              </h3>
              <p style={{ margin: '4px 0', fontSize: '14px' }}>
                <strong>{formatDate(schedule.nextFertilizer)}</strong>
              </p>
              <p style={{ 
                margin: '4px 0', 
                fontSize: '12px', 
                color: schedule.fertilizerDaysLeft < 0 ? '#cc0000' : '#666'
              }}>
                {getDaysText(schedule.fertilizerDaysLeft)}
              </p>
            </div>
          </div>
        ) : (
          <p>No schedule data available</p>
        )}
        <div className="modal-actions">
          <button onClick={onClose}>Close</button>
        </div>
      </div>
    </div>
  );
};

export default PlantSchedule;
